import { ethers } from 'ethers';
import { useEffect, useState } from 'react';
import deploy from './deploy';
import Escrow from './Escrow';
import Deployed from './deployedContracts';
import Dialog from "@material-ui/core/Dialog";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import Button from "@material-ui/core/Button";


const provider = new ethers.providers.Web3Provider(window.ethereum);
 
function App() {
  const [escrows, setEscrows] = useState([]);
  const [account, setAccount] = useState();
  const [signer, setSigner] = useState();
  const [open, setOpen] = useState(false);
  const storedValues = JSON.parse(localStorage.getItem('storedValues')) || [];
  
  const handleClickToOpen = () => {
        setOpen(true);
        
  };
 
  const handleToClose = () => {
       setOpen(false);
  };

  useEffect(() => {
    async function getAccounts() {
      const accounts = await provider.send('eth_requestAccounts', []);

      setAccount(accounts[0]);
      setSigner(provider.getSigner());
    }

    getAccounts();
  }, [account]);

  async function approve(escrowContract, signer) {
    try{
    const approveTxn = await escrowContract.connect(signer).approve();
    await approveTxn.wait();
    }
    catch(e){
      handleClickToOpen();
      
    }
  }

  async function newContract() {
    const beneficiary = document.getElementById('beneficiary').value;
    const arbiter = document.getElementById('arbiter').value;
    var value = ethers.BigNumber.from(document.getElementById('wei').value);
    value = ethers.utils.parseEther(`${value}`);
    const escrowContract = await deploy(signer, arbiter, beneficiary, value);

    const n = {
      address: escrowContract.address,
      arbiter,
      beneficiary,
      value: value.toString()
    }
    storedValues.push(n);
    localStorage.setItem('storedValues', JSON.stringify(storedValues));

    const escrow = {
      address: escrowContract.address,
      arbiter,
      beneficiary,
      value: value.toString(),
      handleApprove: async () => {
        escrowContract.on('Approved', () => {
          document.getElementById(escrowContract.address).className =
            'complete';
          document.getElementById(escrowContract.address).innerText =
            "âœ“ It's been approved!";
        });

        await approve(escrowContract, signer);
      },
    };

    setEscrows([...escrows, escrow]);
  }

  const wrong = ()=>{
      return(
        <Dialog open={open} onClose={handleToClose}>
                <DialogTitle>{"Invalid Arbiter!"}</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        Try Again!
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleToClose}
                        color="primary" autoFocus>
                        Close
                    </Button>
                </DialogActions>
        </Dialog>
      )
  }

  return (
    <>
      <div className="contract">
        <h1> New Contract </h1>
        <label>
          Arbiter Address
          <input type="text" id="arbiter" />
        </label>

        <label>
          Beneficiary Address
          <input type="text" id="beneficiary" />
        </label>

        <label>
          Deposit Amount (in ETH)
          <input type="text" id="wei" />
        </label>

        <div
          className="button"
          id="deploy"
          onClick={(e) => {
            e.preventDefault();

            newContract();
          }}
        >
          Deploy
        </div>
      </div>

      <div className="existing-contracts">
        <h1> Existing Contracts </h1>

        <div id="container">
          {escrows.map((escrow) => {
            return <Escrow key={escrow.address} {...escrow} />;
          })}
        </div>
      </div>

      <div id="container">
          {storedValues.map((escrow) => {
            return <Deployed key={escrow.address} {...escrow} />;
          })}
          
      </div>
    </>
  );
}

export default App;
