import { useEffect, useState, createContext} from 'react';
import { ethers } from 'ethers';
import { Link } from 'react-router-dom';
import deploy from './deploy';

  
export const Appcontext = createContext({});
const provider = new ethers.providers.Web3Provider(window.ethereum);

export async function s(signer,arbiter,beneficiary,value){
  return await deploy(signer, arbiter, beneficiary, value);
}

export function ss(signer,value){
  return {si: signer,v : value};
}

function Home() {

    const [account, setAccount] = useState();
    const [signer, setSigner] = useState();

  
    useEffect(() => {
      async function getAccounts() {
        const accounts = await provider.send('eth_requestAccounts', []);
  
        setAccount(accounts[0]);
        setSigner(provider.getSigner());
      }
  
      getAccounts();
    }, [account]);

    var beneficiary;
    var value;
    var arbiter;

    async function c(){
      beneficiary = document.getElementById('beneficiary').value;
      arbiter = document.getElementById('arbiter').value;
      value = ethers.BigNumber.from(document.getElementById('wei').value);
      value = ethers.utils.parseEther(`${value}`);
      s(signer,arbiter,beneficiary,value);
      ss(signer,value);
    }

    return (
<div className="contract">
        <h1> New Contract </h1>
        <label>
          Arbiter Address
          <input type="text" id="arbiter" />
        </label>

        <label>
          Beneficiary Address
          <input type="text" id="beneficiary" />
        </label>

        <label>
          Deposit Amount (in ETH)
          <input type="text" id="wei" />
        </label>

        <div
          className="button"
          id="deploy"
          onClick={(e) => {
            e.preventDefault();
            c();
          }}
        >
          <Link to={`/approve/data=${encodeURIComponent(signer)}`} style={{color: 'black', textDecoration: 'none'}}>Deploy</Link>
        </div>
      </div>
    )
        }
export default Home;
