import { ethers } from 'ethers';
import { useEffect, useState} from 'react';
import deploy from './deploy';


const provider = new ethers.providers.Web3Provider(window.ethereum);
 
function App() {
  const [escrows, setEscrows] = useState([]);
  const [account, setAccount] = useState();
  const [signer, setSigner] = useState();
  const storedValues = JSON.parse(localStorage.getItem('storedValues')) || [];

  useEffect(() => {
    async function getAccounts() {
      const accounts = await provider.send('eth_requestAccounts', []);

      setAccount(accounts[0]);
      setSigner(provider.getSigner());
    }

    getAccounts();
  }, [account]);

  async function approve(escrowContract, signer) {
    try{
    const approveTxn = await escrowContract.connect(signer).approve();
    await approveTxn.wait();
    }
    catch(e){
        alert("Invalid Arbiter!!");
    }
  }

  async function newContract() {
    const beneficiary = document.getElementById('beneficiary').value;
    const arbiter = document.getElementById('arbiter').value;
    var value = ethers.BigNumber.from(document.getElementById('wei').value);
    value = ethers.utils.parseEther(`${value}`);
    const escrowContract = await deploy(signer, arbiter, beneficiary, value);

    const n = {
      address: escrowContract.address,
      arbiter,
      beneficiary,
      value: value.toString()
    }
    storedValues.push(n);
    localStorage.setItem('storedValues', JSON.stringify(storedValues));

    const escrow = {
      address: escrowContract.address,
      arbiter,
      beneficiary,
      value: value.toString(),
      handleApprove: async () => {
        escrowContract.on('Approved', () => {
          document.getElementById(escrowContract.address).className =
            'complete';
          document.getElementById(escrowContract.address).innerText =
            "✓ It's been approved!";
        });

        await approve(escrowContract, signer);
      },
    };

    setEscrows([...escrows, escrow]);
  }


  return (
    <>
      <div className="contract">
        <h1> New Contract </h1>
        <label>
          Arbiter Address
          <input type="text" id="arbiter" />
        </label>

        <label>
          Beneficiary Address
          <input type="text" id="beneficiary" />
        </label>

        <label>
          Deposit Amount (in ETH)
          <input type="text" id="wei" />
        </label>

        <div
          className="button"
          id="deploy"
          onClick={(e) => {
            e.preventDefault();

            newContract();
          }}
        >
          Deploy
        </div>
      </div>
    </>
  );
}

export default App;
