import { useEffect, useState, createContext} from 'react';
import { ethers } from 'ethers';
import { Link } from 'react-router-dom';
  
export const Appcontext = createContext(null);
const provider = new ethers.providers.Web3Provider(window.ethereum);

function Home() {

    const [account, setAccount] = useState();
    const [signer, setSigner] = useState();
  
    
    useEffect(() => {
      async function getAccounts() {
        const accounts = await provider.send('eth_requestAccounts', []);
  
        setAccount(accounts[0]);
        setSigner(provider.getSigner());
      }
  
      getAccounts();
    }, [account]);

    var beneficiary;
    var value;
    var arbiter;
    function c(){
    beneficiary = document.getElementById('beneficiary').value;
    arbiter = document.getElementById('arbiter').value;
    value = ethers.BigNumber.from(document.getElementById('wei').value);
    value = ethers.utils.parseEther(`${value}`);
    }
    return (
<div className="contract">
        <h1> New Contract </h1>
        <label>
          Arbiter Address
          <input type="text" id="arbiter" />
        </label>

        <label>
          Beneficiary Address
          <input type="text" id="beneficiary" />
        </label>

        <label>
          Deposit Amount (in ETH)
          <input type="text" id="wei" />
        </label>

        <div
          className="button"
          id="deploy"
          onClick={(e) => {
            e.preventDefault();
            c();
          }}
        >
          <Link to={`/approve/`} style={{color: 'black', textDecoration: 'none'}}>Deploy</Link>
        </div>
        <Appcontext.Provider vslue={{beneficiary,arbiter,value,signer}}>

        </Appcontext.Provider>
      </div>
    )
        }
export default Home;
